choose.when:
update freeboard 
set commentCount = commentCount + 1 
where freeboardId = #{bno}

update tradeboard 
set commentCount = commentCount + 1 
where tradeboardId = #{bno}

update recruitboard 
set commentCount = commentCount + 1 
where recruitboardId = #{bno}

update freeboard 
set commentCount = commentCount - 1 
where freeboardId = #{bno}

update tradeboard 
set commentCount = commentCount - 1 
where tradeboardId = #{bno}

update recruitboard 
set commentCount = commentCount - 1 
where recruitboardId = #{bno}

mapper.update:
update comments 
set content = #{content}, regdate = NOW(), secret = #{secret}
where commentsId = #{commentsId}
