mapper.select:
SELECT COUNT(DISTINCT r.userId) FROM leagueRank r, user u
r.userId = u.userId AND r.insertdate >= #{startDate} AND r.insertdate <= #{endDate}
AND u.username LIKE CONCAT ('%', #{searchKeyword}, '%')

SELECT * FROM ( SELECT (ROW_NUMBER() OVER (ORDER BY (sum(r.wincount) - sum(r.losecount)) DESC) ) AS idx, r.userId, sum(r.wincount) AS wincount, sum(r.losecount) AS losecount, u.username, u.badgeId FROM leagueRank r, user u WHERE r.userId = u.userId AND r.insertdate >= #{startDate} AND r.insertdate <= #{endDate} GROUP BY r.userId) t
WHERE username LIKE CONCAT ('%', #{searchKeyword}, '%')
LIMIT #{start}, #{amount}
