mapper.select:
SELECT tradeboard.*, badge.badgeName FROM tradeboard INNER JOIN user ON tradeboard.userId = user.userId INNER JOIN badge ON user.badgeId = badge.badgeId
(deleteYN IS NULL OR deleteYN != 1)
AND (tradeboard.userId NOT IN (SELECT blockedUser FROM blockuser WHERE blockuser.userId = #{userId}))
AND itemClass = #{itemClass}
AND title LIKE CONCAT('%', #{searchKeyword}, '%')
AND tradeboard.userId LIKE CONCAT('%', #{searchKeyword}, '%')
AND content LIKE CONCAT('%', #{searchKeyword}, '%')
AND title LIKE CONCAT('%', #{searchKeyword}, '%') AND region = #{region}
AND tradeboard.userId LIKE CONCAT('%', #{searchKeyword}, '%') AND region = #{region}
AND content LIKE CONCAT('%', #{searchKeyword}, '%') AND region = #{region}
AND region = #{region}
ORDER BY tradeboardId DESC
ORDER BY regdate DESC
ORDER BY hit DESC
ORDER BY `like` DESC
LIMIT #{start}, #{amount}

select COUNT(tradeboardId) from tradeboard

	(deleteYN IS NULL OR deleteYN != 1)

		AND (userId NOT IN (SELECT blockedUser FROM blockuser
		WHERE userId = #{userId}))


		AND itemClass = #{itemClass}





					AND title LIKE CONCAT('%', #{searchKeyword}, '%')


					AND userId LIKE CONCAT('%', #{searchKeyword}, '%')


					AND content LIKE CONCAT('%', #{searchKeyword}, '%')






					AND title LIKE CONCAT('%', #{searchKeyword}, '%') AND region =
					#{region}


					AND userId LIKE CONCAT('%', #{searchKeyword}, '%') AND region =
					#{region}


					AND content LIKE CONCAT('%', #{searchKeyword}, '%') AND region =
					#{region}




			AND region = #{region}

mapper.update:
update tradeboard 
set title = #{title}, content = #{content}, 
	region = #{region}, regdate = NOW(), complete = #{complete}, itemClass = #{itemClass}, imageName = #{imageName}
where tradeboardId = #{tradeboardId}
